// CourseCarousel.js
import React from 'react';
import Slider from 'react-slick';
import CourseCard from './CourseCard'; // Import your existing card component

import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';

const carousel = () => {
  const cardData = [
    {
        courseCode: 'CIS101',
        courseName: 'Introduction to Computer Science',
        professorName: 'Dr. Smith',
        enrollment: 120,
        avgGrade: 'A-',
      },
      {
        courseCode: 'MAT202',
        courseName: 'Linear Algebra',
        professorName: 'Prof. Johnson',
        enrollment: 90,
        avgGrade: 'B+',
      },
    {
        courseCode: 'CIS101',
        courseName: 'Introduction to Computer Science',
        professorName: 'Dr. Smith',
        enrollment: 120,
        avgGrade: 'A-',
      },
      {
        courseCode: 'MAT202',
        courseName: 'Linear Algebra',
        professorName: 'Prof. Johnson',
        enrollment: 90,
        avgGrade: 'B+',
      },
  ];

  const settings = {
    dots: false, // Disable dots
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    prevArrow: <Arrow direction="prev" />, // Custom previous arrow
    nextArrow: <Arrow direction="next" />, // Custom next arrow
  };

  const Arrow = ({ direction, onClick }) => (
    <div className={`arrow ${direction}`} onClick={onClick}>
      {direction === 'prev' ? '<' : '>'}
    </div>
  );

  return (
    <Slider {...settings}>
      {cardData.map((data, index) => (
        <div key={index}>
          <CourseCard {...data} />
        </div>
      ))}
    </Slider>
  );
};

export default carousel
